// import library
import React, {Component} from 'react';
import Alert from './Alert';

class Bmi extends Component {

	constructor(props){
		super(props);
		this.state = {
			berat: 60,
			tinggi: 170
		};

		this.BeratChange = this.BeratChange.bind(this);
		this.TinggiChange = this.TinggiChange.bind(this);
		this.hitungBmi = this.hitungBmi.bind(this);
	}

	BeratChange(event) {
		this.setState({berat: event.target.value})
	}

	TinggiChange(event){
		this.setState({tinggi: event.target.value})
	}

	hitungBmi = (event) => {
		let tinggi = this.state.tinggi;
		let berat = this.state.berat;

		// kalkulasi
		tinggi /= 100; // Untuk mengkonversi CM -> M
		let hasil = berat / (tinggi*tinggi);

		// ifelse
		if(hasil <= 18.5){
			document.getElementById('hasilF').innerHTML = "<p>Anda Kurus</p>";
	  } else if(hasil >= 18.5 && hasil <= 22.9){
			document.getElementById('hasilF').innerHTML = "<p>Anda Normal</p>";
	  } else if(hasil >= 23 && hasil <= 24.9){
			document.getElementById('hasilF').innerHTML = "<p>Anda Obesitas</p>";
	  } else{
			document.getElementById('hasilF').innerHTML = "<p>Anda Tidak Normal</p>";
	  }

		event.preventDefault();
	}

	render(){
		return(
			<div className="container">
				<div className="row">
					<div className="card col-sm-5 mx-auto">
						<div className="card-header text-center">
							<h4>Body Mass Index</h4>
						</div>
						<div className="card-body">
							<div className="row">
								<p className="col-6">Berat (kg)</p>
								<input type="number" name="berat" className="col form-control" value={this.state.berat} onChange={this.BeratChange}/>
								<p className="col-6">Tinggi (cm)</p>
								<input type="number" name="tinggi" className="col form-control" value={this.state.tinggi} onChange={this.TinggiChange}/>
							</div>
						</div>
						<div className="card-footer">
							<button className="form-control btn btn-info" onClick={this.hitungBmi} >
								Hitung
							</button>
							<h4 className="text-center mt-4">Hasil</h4>
							{/* Alert */}
							<div className="alert alert-info alert-dismissible fade show" role="alert">
								<strong id="hasilF"></strong>
								<button type="button" className="close" data-dismiss="alert" aria-label="Close">
									<span aria-hidden="true">&times;</span>
								</button>
							</div>
						</div>
					</div>
				</div>
			</div>
		);
	}
}

export default Bmi;
