// import library
import React, {Component} from 'react';

class Bmi extends Component {

	constructor(){
		super();
		this.state = {
			berat: 60,
			tinggi: 170,
			hasil: "Ideal",
			content: "Mantap"
		}
	}

	hitungBmi = (event) => {
		this.setState({
			berat: event.target.value,
			tinggi: event.target.value,
		})
	}

	render(){
		return(
			<div className="container">
				<div className="row">
					<div className="card col-sm-5 mx-auto">
						<div className="card-header text-center">
							<h4>Body Mass Index</h4>
						</div>
						<div className="card-body">
							<div className="row">
								<p className="col-6">Berat (kg)</p>
								<input type="number" name="berat" className="col form-control" value={this.state.berat} />
								<p className="col-6">Tinggi (cm)</p>
								<input type="number" name="tinggi" className="col form-control" />
							</div>
						</div>
						<div className="card-footer">
							<button className="form-control btn btn-info" onClick={this.hitungBmi} >
								Hitung
							</button>
							<h4 className="text-center mt-4">Hasil</h4>
							{/* Alert */}
							<div className="alert alert-info alert-dismissible fade show" role="alert">
								<strong>{this.state.hasil}</strong> {this.state.content} {this.state.berat} {this.state.tinggi}
								<button type="button" className="close" data-dismiss="alert" aria-label="Close">
									<span aria-hidden="true">&times;</span>
								</button>
							</div>
						</div>
					</div>
				</div>
			</div>
		);
	}
}

export default Bmi;
