// import library
import React, {Component} from 'react';

// class
class Cicilan extends Component {
    constructor(){
        super();
        this.state = {
            nominal: '',
            bunga: '',
            periode: '',
            hasil: ''
        };

        this.nominalChange = this.nominalChange.bind(this);
        this.bungaChange = this.bungaChange.bind(this);
        this.periodeChange = this.periodeChange.bind(this);
    }

    nominalChange(event){
        this.setState({nominal: event.target.value})
    }

    bungaChange(event){
        this.setState({bunga: event.target.value})
    }

    periodeChange(event){
        this.setState({periode: event.target.value})
    }

    hitung = (event)  => {
        let nominal = this.state.nominal;
        let bunga = this.state.bunga;
        let periode = this.state.periode;

        // kalkulasi
        let cicilan = (nominal/periode) + (nominal/100*bunga); //Cicilan + jumlah bunga

        //output
        this.setState({hasil: cicilan})

        event.preventDefault();
    }

    render(){
        return(
            <div className="card col-sm-5 mx-auto m-5">
				<div className="card-header text-center bg-info text-white">
					<h4>Cicilan Bank</h4>
				</div>
				<div className="card-body">
					<div className="row">
						<p className="col-6">Nominal</p>
                        <input className="col form-control" type="number" onChange={this.nominalChange} />
                        <p className="col-6">Bunga (%)</p>
                        <input className="col form-control" type="number" onChange={this.bungaChange}/>
                        <p className="col-6">Periode</p>
                        <input className="col form-control" type="number" onChange={this.periodeChange}/>
					</div>
				</div>
				<div className="card-footer">
					<button className="form-control btn btn-info" onClick={this.hitung}>
						Hitung
					</button>
					<h4 className="text-center mt-4">Hasil</h4>
					<input className="form-control" value={this.state.hasil} readOnly/>
				</div>
			</div>
        );
    }
}

export default Cicilan;
