// import library
import React, {Component} from 'react';
import Bmi from './Components/Bmi';

class App extends Component{
  render(){
    return(
      <div className="container">
          <div className="row">
              <div className="card col-sm-5 mx-auto">
                  <div className="card-header text-center">
                      <h4>Body Mass Index</h4>
                  </div>
                  <div className="card-body">
                  <div className="row">
                      <p className="col-6">Berat (kg)</p>
                      <input type="number" name="berat" id="berat" className="col form-control" />
                      <p className="col-6">Tinggi (cm)</p>
                      <input type="number" name="tinggi" id="tinggi" className="col form-control" />
                  </div>
                  </div>
                  <div className="card-footer">
                  <button className="form-control btn btn-info" onclick="hitung();" >
                      Hitung
                  </button>
                  <h4 className="text-center mt-4">Hasil</h4>
                  <input type="text" id="hasil" className="form-control text-center" readonly />
                  </div>
              </div>
          </div>
      </div>
    );
  }
}

export default App;
